    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String informacion = "DNI: " + jTextField1.getText();
        
        informacion+= "\n nombre:  " + jTextField2.getText();
        
        informacion+= "\n apellido: " + jTextField3.getText();
        
        informacion+= "\n fecha de nacimiento: " + jComboBox1.getSelectedItem() + " de " + jComboBox2.getSelectedItem() + "del a√±o " + jComboBox3.getSelectedItem();
        
        JOptionPane.showMessageDialog(this, informacion,"Muestra de datos", JOptionPane.INFORMATION_MESSAGE);
    }//GEN-LAST:event_jButton1ActionPerformed

